import React, { useState } from 'react';
import axios from 'axios';

const MessageComposer = ({ onMessageSent }) => {
  const [formData, setFormData] = useState({
    content: '',
    phoneNumber: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSendingSms, setIsSendingSms] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setMessage('');

    try {
      const response = await axios.post('/api/messages', formData);
      onMessageSent(response.data);
      setMessage('Message saved successfully!');
      setFormData({
        content: '',
        phoneNumber: ''
      });
    } catch (error) {
      setMessage('Error saving message. Please try again.');
      console.error('Error:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleSendSms = async () => {
    if (!formData.content || !formData.phoneNumber) {
      setMessage('Please fill in both phone number and message content');
      return;
    }

    setIsSendingSms(true);
    setMessage('');

    try {
      const response = await axios.post('/api/messages/send-sms', formData);

      if (response.data.statusCode === 200) {
        const body = response.data.body;
        if (body && body.success) {
          setMessage(`SMS sent successfully! Message ID: ${body.data?.shootId || 'N/A'}`);
        } else {
          setMessage('SMS sent, but response indicates failure.');
        }
      } else {
        setMessage(`SMS sending failed with status ${response.data.statusCode}`);
      }
    } catch (error) {
      setMessage('Error sending SMS. Please try again.');
      console.error('Error:', error);
    } finally {
      setIsSendingSms(false);
    }
  };

  return (
    <div>
      <h3>Compose Message</h3>
      <p>Send a message to a customer by entering their phone number and message content.</p>

      {message && (
        <div className={`notification ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="phoneNumber">Phone Number *</label>
          <input
            type="tel"
            id="phoneNumber"
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleChange}
            required
            placeholder="Enter customer's phone number"
          />
        </div>

        <div className="form-group">
          <label htmlFor="content">Message Content *</label>
          <textarea
            id="content"
            name="content"
            value={formData.content}
            onChange={handleChange}
            required
            rows="6"
            placeholder="Enter your message to the customer"
          />
        </div>

        <button 
          type="submit" 
          className="btn"
          disabled={isSubmitting}
        >
          {isSubmitting ? 'Sending...' : 'Send Message'}
        </button>
      </form>
    </div>
  );
};

export default MessageComposer;
