import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import ParcelRequestList from './ParcelRequestList';
import MessageComposer from './MessageComposer';
import MessageList from './MessageList';
import NotificationService from '../services/NotificationService';

const AdminDashboard = () => {
  const [parcelRequests, setParcelRequests] = useState([]);
  const [messages, setMessages] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const [search, setSearch] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  useEffect(() => {
    fetchData();
    setupWebSocket();
  }, []);

  const fetchData = async () => {
    try {
      const [requestsResponse, messagesResponse, unreadResponse] = await Promise.all([
        axios.get('/api/parcel-requests'),
        axios.get('/api/messages/sms'),
        axios.get('/api/parcel-requests/unread-count')
      ]);

      setParcelRequests(requestsResponse.data);
      setMessages(messagesResponse.data);
      setUnreadCount(unreadResponse.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const setupWebSocket = () => {
    NotificationService.connect((notification) => {
      if (notification.type === 'new_request') {
        setParcelRequests(prev => [notification.data, ...prev]);
        setUnreadCount(prev => prev + 1);
      }
    });
  };

  const handleMarkAsRead = async (id) => {
    try {
      await axios.put(`/api/parcel-requests/${id}/mark-read`);
      setParcelRequests(prev => 
        prev.map(req => req.id === id ? { ...req, isRead: true } : req)
      );
      setUnreadCount(prev => Math.max(0, prev - 1));
    } catch (error) {
      console.error('Error marking as read:', error);
    }
  };

  const handleMessageSent = (newMessage) => {
    setMessages(prev => [newMessage, ...prev]);
  };

  // Metrics
  const { pendingCount, deliveredCount, failedCount } = useMemo(() => {
    const toLower = (v) => (v || '').toString().toLowerCase();
    let pending = 0, delivered = 0, failed = 0;
    for (const m of messages) {
      const s = toLower(m.status);
      if (s === 'delivered') delivered++;
      else if (s === 'failed' || s === 'error') failed++;
      else pending++;
    }
    return { pendingCount: pending, deliveredCount: delivered, failedCount: failed };
  }, [messages]);

  // Filters
  const filteredMessages = useMemo(() => {
    const q = search.trim().toLowerCase();
    return messages.filter(m => {
      const matchesQuery = !q || (m.phoneNumber?.toLowerCase().includes(q) || m.message?.toLowerCase().includes(q));
      const s = (m.status || '').toLowerCase();
      const matchesStatus = statusFilter === 'all' || s === statusFilter;
      return matchesQuery && matchesStatus;
    });
  }, [messages, search, statusFilter]);

  if (loading) {
    return (
      <div className={`dashboard-wrapper ${sidebarOpen ? 'sidebar-open' : ''}`}>
        {sidebarOpen && <div className="sidebar-overlay" onClick={() => setSidebarOpen(false)}></div>}
        <aside className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
          <div className="sidebar-header">
            <div className="sidebar-logo">
              <span className="logo-icon">ğŸ›¡ï¸</span>
              <h2>Admin Panel</h2>
            </div>
            <button className="sidebar-toggle" onClick={() => setSidebarOpen(!sidebarOpen)}>
              {sidebarOpen ? 'Ã—' : 'â‰¡'}
            </button>
          </div>
        </aside>
        <main className="dashboard-main">
          <button 
            className="mobile-menu-btn"
            onClick={() => setSidebarOpen(true)}
          >
            â‰¡
          </button>
          <div className="top-header">
            <h1 className="page-title">Dashboard</h1>
            <div className="header-actions">
              <button className="header-icon-btn">
                <span className="icon">ğŸ””</span>
              </button>
              <button className="header-icon-btn active">
                <span className="icon">ğŸ‘¤</span>
              </button>
              <button className="logout-btn">Logout</button>
            </div>
          </div>
          <div className="dashboard-content">
            <div className="card">
              <p>Loading...</p>
            </div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className={`dashboard-wrapper ${sidebarOpen ? 'sidebar-open' : ''}`}>
      {sidebarOpen && <div className="sidebar-overlay" onClick={() => setSidebarOpen(false)}></div>}
      <aside className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <div className="sidebar-logo">
            <span className="logo-icon">ğŸ›¡ï¸</span>
            <h2>Admin Panel</h2>
          </div>
          <button className="sidebar-toggle" onClick={() => setSidebarOpen(!sidebarOpen)}>
            {sidebarOpen ? 'Ã—' : 'â‰¡'}
          </button>
        </div>
        <nav className="sidebar-nav">
          <button
            className={`nav-item ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => {
              setActiveTab('dashboard');
              if (window.innerWidth <= 768) setSidebarOpen(false);
            }}
          >
            <span className="nav-icon">ğŸ </span>
            <span className="nav-text">Dashboard</span>
          </button>
          <button
            className={`nav-item ${activeTab === 'messages' ? 'active' : ''}`}
            onClick={() => {
              setActiveTab('messages');
              if (window.innerWidth <= 768) setSidebarOpen(false);
            }}
          >
            <span className="nav-icon">ğŸ“„</span>
            <span className="nav-text">Messages</span>
            {messages.length > 0 && <span className="nav-badge">{messages.length}</span>}
          </button>
          <button
            className={`nav-item ${activeTab === 'requests' ? 'active' : ''}`}
            onClick={() => {
              setActiveTab('requests');
              if (window.innerWidth <= 768) setSidebarOpen(false);
            }}
          >
            <span className="nav-icon">ğŸ“¦</span>
            <span className="nav-text">Parcel Requests</span>
            {unreadCount > 0 && <span className="nav-badge unread">{unreadCount}</span>}
          </button>
          <button
            className={`nav-item ${activeTab === 'compose' ? 'active' : ''}`}
            onClick={() => {
              setActiveTab('compose');
              if (window.innerWidth <= 768) setSidebarOpen(false);
            }}
          >
            <span className="nav-icon">âœï¸</span>
            <span className="nav-text">Compose Message</span>
          </button>
          <button
            className={`nav-item ${activeTab === 'users' ? 'active' : ''}`}
            onClick={() => {
              setActiveTab('users');
              if (window.innerWidth <= 768) setSidebarOpen(false);
            }}
          >
            <span className="nav-icon">ğŸ‘¥</span>
            <span className="nav-text">Users</span>
          </button>
          <button
            className={`nav-item ${activeTab === 'settings' ? 'active' : ''}`}
            onClick={() => {
              setActiveTab('settings');
              if (window.innerWidth <= 768) setSidebarOpen(false);
            }}
          >
            <span className="nav-icon">âš™ï¸</span>
            <span className="nav-text">Settings</span>
          </button>
          <button
            className={`nav-item ${activeTab === 'reports' ? 'active' : ''}`}
            onClick={() => {
              setActiveTab('reports');
              if (window.innerWidth <= 768) setSidebarOpen(false);
            }}
          >
            <span className="nav-icon">ğŸ“Š</span>
            <span className="nav-text">Reports</span>
          </button>
        </nav>
      </aside>
      <main className="dashboard-main">
        <button 
          className="mobile-menu-btn"
          onClick={() => setSidebarOpen(true)}
        >
          â‰¡
        </button>

        {/* Top Header Bar */}
        <div className="top-header">
          <h1 className="page-title">Dashboard</h1>
          <div className="header-actions">
            <button className="header-icon-btn">
              <span className="icon">ğŸ””</span>
            </button>
            <button className="header-icon-btn active">
              <span className="icon">ğŸ‘¤</span>
            </button>
            <button className="logout-btn">Logout</button>
          </div>
        </div>

        <div className="dashboard-content">
          {activeTab === 'dashboard' && (
            <>
              {/* Stat Cards */}
              <div className="stats-row">
                <div className="stat-card-modern">
                  <div className="stat-icon blue">ğŸ“„</div>
                  <div className="stat-info">
                    <div className="stat-label">Number of Sections</div>
                    <div className="stat-number">{parcelRequests.length}</div>
                  </div>
                </div>
                <div className="stat-card-modern">
                  <div className="stat-icon green">ğŸ‘ï¸</div>
                  <div className="stat-info">
                    <div className="stat-label">Number of Content</div>
                    <div className="stat-number">{messages.length}</div>
                  </div>
                </div>
                <div className="stat-card-modern">
                  <div className="stat-icon yellow">ğŸš«</div>
                  <div className="stat-info">
                    <div className="stat-label">Number of Invisible Content</div>
                    <div className="stat-number">{failedCount}</div>
                  </div>
                </div>
              </div>

              {/* Content Grid */}
              <div className="content-grid">
                {/* Recent Activity Card */}
                <div className="content-card">
                  <div className="card-header">
                    <h3 className="card-title">Recent Activity</h3>
                    <a href="#" className="view-all-link">View All</a>
                  </div>
                  <div className="activity-list">
                    <div className="activity-item">
                      <span className="activity-icon">ğŸ‘¤</span>
                      <div className="activity-content">
                        <div className="activity-text">New user registered</div>
                        <div className="activity-time">2 minutes ago</div>
                      </div>
                    </div>
                    <div className="activity-item">
                      <span className="activity-icon">âœ…</span>
                      <div className="activity-content">
                        <div className="activity-text">System update completed</div>
                        <div className="activity-time">1 hour ago</div>
                      </div>
                    </div>
                    <div className="activity-item">
                      <span className="activity-icon">âš ï¸</span>
                      <div className="activity-content">
                        <div className="activity-text">Security alert resolved</div>
                        <div className="activity-time">3 hours ago</div>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Quick Actions Card */}
                <div className="content-card">
                  <div className="card-header">
                    <h3 className="card-title">Quick Actions</h3>
                  </div>
                  <div className="quick-actions-grid">
                    <button className="quick-action-btn">
                      <span className="action-icon">â•</span>
                      <span className="action-text">Add User</span>
                    </button>
                    <button className="quick-action-btn">
                      <span className="action-icon">âš™ï¸</span>
                      <span className="action-text">Settings</span>
                    </button>
                    <button className="quick-action-btn">
                      <span className="action-icon">ğŸ“„</span>
                      <span className="action-text">Generate Report</span>
                    </button>
                    <button className="quick-action-btn">
                      <span className="action-icon">ğŸ“¤</span>
                      <span className="action-text">Export Data</span>
                    </button>
                  </div>
                </div>
              </div>
            </>
          )}

          {activeTab === 'messages' && (
            <>
              <div className="card" style={{ marginBottom: 16 }}>
                <div className="filters">
                  <input
                    className="input"
                    placeholder="Search by phone or content..."
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                    style={{ flex: 1 }}
                  />
                  <select
                    className="select"
                    value={statusFilter}
                    onChange={(e) => setStatusFilter(e.target.value)}
                  >
                    <option value="all">All statuses</option>
                    <option value="pending">Pending</option>
                    <option value="delivered">Delivered</option>
                    <option value="failed">Failed</option>
                    <option value="error">Error</option>
                  </select>
                </div>
              </div>
              <div className="card">
                <MessageList messages={filteredMessages} />
              </div>
            </>
          )}

          {activeTab === 'requests' && (
            <div className="card">
              <ParcelRequestList
                requests={parcelRequests}
                onMarkAsRead={handleMarkAsRead}
              />
            </div>
          )}

          {activeTab === 'compose' && (
            <div className="card">
              <MessageComposer onMessageSent={handleMessageSent} />
            </div>
          )}

          {(activeTab === 'users' || activeTab === 'settings' || activeTab === 'reports') && (
            <div className="card">
              <h2>{activeTab.charAt(0).toUpperCase() + activeTab.slice(1)}</h2>
              <p>This section is coming soon.</p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default AdminDashboard;
