import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = ({ onLogin }) => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // TODO: Replace with actual authentication endpoint
      // For now, using a simple check - you'll need to implement backend auth
      const response = await axios.post('/api/auth/login', {
        phoneNumber,
        password
      });

      if (response.data.success) {
        // Store auth token or session
        localStorage.setItem('authToken', response.data.token || 'authenticated');
        localStorage.setItem('user', JSON.stringify(response.data.user || { phoneNumber }));
        onLogin(true);
        navigate('/admin');
      } else {
        setError(response.data.message || 'Invalid credentials');
      }
    } catch (err) {
      // If backend doesn't have auth endpoint yet, use simple validation
      if (err.response?.status === 404 || err.code === 'ERR_NETWORK') {
        // Temporary: Simple client-side validation for demo
        if (phoneNumber && password) {
          localStorage.setItem('authToken', 'authenticated');
          localStorage.setItem('user', JSON.stringify({ phoneNumber }));
          onLogin(true);
          navigate('/admin');
        } else {
          setError('Please enter both phone number and password');
        }
      } else {
        setError(err.response?.data?.message || 'Login failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container" style={{ maxWidth: '400px', marginTop: '100px' }}>
      <div className="card">
        <div style={{ textAlign: 'center', marginBottom: '24px' }}>
          <h2 style={{ color: '#4B0082', marginBottom: '8px' }}>NILETEE</h2>
          <p className="subtle">Admin Login</p>
        </div>

        {error && (
          <div className="notification error" style={{ marginBottom: '16px' }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="label-required">Phone Number</label>
            <input
              type="tel"
              className="input"
              placeholder="Enter phone number"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
              required
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label className="label-required">Password</label>
            <input
              type="password"
              className="input"
              placeholder="Enter password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              disabled={loading}
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary"
            style={{ width: '100%', marginTop: '8px' }}
            disabled={loading}
          >
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
